import * as types from "../../actionTypes";
import SkolaApi from "../../../api/SkolaApi";

let actionsFunction = {

  //CRUD METHODS

  // Create skola
  createSkola: function(skola) {
    return function(dispatch) {
      return SkolaApi
        .createSkola(skola)
        .then(skola => {
          dispatch(actionsFunction.createSkolaSuccess(skola));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createSkolaSuccess: function(skola) {
    return { type: types.CREATE_SKOLA_SUCCESS, payload: skola };
  },


  // Delete skola
  deleteSkola: function(id) {
    return function(dispatch) {
      return SkolaApi
        .deleteSkola(id)
        .then(skola => {
          dispatch(actionsFunction.deleteSkolaSuccess(skola));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteSkolaSuccess: function(skola) {
    return { type: types.DELETE_SKOLA_SUCCESS, payload: skola };
  },


  // Get skola
  loadSkola: function(id) {
    return function(dispatch) {
      return SkolaApi
        .getOneSkola(id)
        .then(skola => {
          dispatch(actionsFunction.loadSkolaSuccess(skola));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadSkolaSuccess: function(skola) {
    return { type: types.GET_SKOLA_SUCCESS, payload: skola };
  },

  // Load  list
  loadSkolaList: function() {
    return function(dispatch) {
      return SkolaApi
        .getSkolaList()
        .then(list => {
          dispatch(actionsFunction.loadSkolaListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadSkolaListSuccess: function(list) {
    return { type: types.LIST_SKOLA_SUCCESS, payload: list };
  },

	
  // Save skola
  saveSkola: function(skola) {
    return function(dispatch) {
      return SkolaApi
        .saveSkola(skola)
        .then(skola => {
          dispatch(actionsFunction.saveSkolaSuccess(skola));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveSkolaSuccess: function(skola) {
    return { type: types.UPDATE_SKOLA_SUCCESS, payload: skola };
  },


};

export default actionsFunction;
