import * as types from "../../actionTypes";
import UdalostApi from "../../../api/UdalostApi";

let actionsFunction = {

  //CRUD METHODS

  // Create udalost
  createUdalost: function(udalost) {
    return function(dispatch) {
      return UdalostApi
        .createUdalost(udalost)
        .then(udalost => {
          dispatch(actionsFunction.createUdalostSuccess(udalost));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createUdalostSuccess: function(udalost) {
    return { type: types.CREATE_UDALOST_SUCCESS, payload: udalost };
  },


  // Delete udalost
  deleteUdalost: function(id) {
    return function(dispatch) {
      return UdalostApi
        .deleteUdalost(id)
        .then(udalost => {
          dispatch(actionsFunction.deleteUdalostSuccess(udalost));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteUdalostSuccess: function(udalost) {
    return { type: types.DELETE_UDALOST_SUCCESS, payload: udalost };
  },


  // Get udalost
  loadUdalost: function(id) {
    return function(dispatch) {
      return UdalostApi
        .getOneUdalost(id)
        .then(udalost => {
          dispatch(actionsFunction.loadUdalostSuccess(udalost));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadUdalostSuccess: function(udalost) {
    return { type: types.GET_UDALOST_SUCCESS, payload: udalost };
  },

  // Load  list
  loadUdalostList: function() {
    return function(dispatch) {
      return UdalostApi
        .getUdalostList()
        .then(list => {
          dispatch(actionsFunction.loadUdalostListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadUdalostListSuccess: function(list) {
    return { type: types.LIST_UDALOST_SUCCESS, payload: list };
  },

	
  // Save udalost
  saveUdalost: function(udalost) {
    return function(dispatch) {
      return UdalostApi
        .saveUdalost(udalost)
        .then(udalost => {
          dispatch(actionsFunction.saveUdalostSuccess(udalost));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveUdalostSuccess: function(udalost) {
    return { type: types.UPDATE_UDALOST_SUCCESS, payload: udalost };
  },


};

export default actionsFunction;
